pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../../interfaces/IDependencyController.sol";

// we chose not to go with an enum
// to make this list easy to extend
uint256 constant FUND_TRANSFERER = 1;
uint256 constant MINTER_BURNER = 2;
uint256 constant TRANCHE = 3;
uint256 constant ORACLE_LISTENER = 4;
uint256 constant TRANCHE_TRANSFERER = 5;
uint256 constant UNDERWATER_LIQUIDATOR = 6;
uint256 constant LIQUIDATION_PROTECTED = 7;
uint256 constant SMART_LIQUIDITY = 8;
uint256 constant LIQUID_YIELD = 9;

uint256 constant PROTOCOL_TOKEN = 100;
uint256 constant FUND = 101;
uint256 constant STABLECOIN = 102;
uint256 constant FEE_RECIPIENT = 103;
uint256 constant STRATEGY_REGISTRY = 104;
uint256 constant TRANCHE_ID_SERVICE = 105;
uint256 constant ORACLE_REGISTRY = 106;
uint256 constant ISOLATED_LENDING = 107;
uint256 constant TWAP_ORACLE = 108;
uint256 constant CURVE_POOL = 109;
uint256 constant ISOLATED_LENDING_LIQUIDATION = 110;
uint256 constant STABLE_LENDING = 111;
uint256 constant STABLE_LENDING_LIQUIDATION = 112;
uint256 constant SMART_LIQUIDITY_FACTORY =  113;
uint256 constant LIQUID_YIELD_HOLDER = 114;
uint256 constant LIQUID_YIELD_REBALANCER = 115;
uint256 constant LIQUID_YIELD_REDISTRIBUTOR_MAVAX = 116;
uint256 constant LIQUID_YIELD_REDISTRIBUTOR_MSAVAX = 117;
uint256 constant INTEREST_RATE_CONTROLLER = 118;

uint256 constant DIRECT_LIQUIDATOR = 200;
uint256 constant LPT_LIQUIDATOR = 201;
uint256 constant DIRECT_STABLE_LIQUIDATOR = 202;
uint256 constant LPT_STABLE_LIQUIDATOR = 203;

uint256 constant DISABLER = 1001;
uint256 constant DEPENDENCY_CONTROLLER = 1002;
uint256 constant ACTIVATOR = 1003;

/// @title Manage permissions of contracts and ownership of everything
/// owned by a multisig wallet during
/// beta and will then be transfered to governance
contract Roles is Ownable {
    mapping(address => mapping(uint256 => bool)) public roles;
    mapping(uint256 => address) public mainCharacters;

    event RoleGiven(uint256 indexed role, address player);
    event CharacterAssigned(
        uint256 indexed character,
        address playerBefore,
        address playerNew
    );
    event RoleRemoved(uint256 indexed role, address player);

    constructor(address targetOwner) Ownable() {
        transferOwnership(targetOwner);
    }

    /// @dev Throws if called by any account other than the owner.
    modifier onlyOwnerExecDepController() {
        require(
            owner() == msg.sender ||
                executor() == msg.sender ||
                mainCharacters[DEPENDENCY_CONTROLLER] == msg.sender,
            "Roles: caller is not the owner"
        );
        _;
    }

    /// @dev assign role to an account
    function giveRole(uint256 role, address actor)
        external
        onlyOwnerExecDepController
    {
        emit RoleGiven(role, actor);
        roles[actor][role] = true;
    }

    /// @dev revoke role of a particular account
    function removeRole(uint256 role, address actor)
        external
        onlyOwnerExecDepController
    {
        emit RoleRemoved(role, actor);
        roles[actor][role] = false;
    }

    /// @dev set main character
    function setMainCharacter(uint256 role, address actor)
        external
        onlyOwnerExecDepController
    {
        emit CharacterAssigned(role, mainCharacters[role], actor);
        mainCharacters[role] = actor;
    }

    /// @dev returns the current executor
    function executor() public returns (address exec) {
        address depController = mainCharacters[DEPENDENCY_CONTROLLER];
        if (depController != address(0)) {
            exec = IDependencyController(depController).currentExecutor();
        }
    }
}
","keccak256":"0xdcd23ffa32837f32274bb3aac78c9c148dfa3e62d56a60333842e1ad5d43dc98","license":"BUSL-1.1"},"contracts/smart-liquidity/CurvePoolSL2.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "../roles/DependsOnStableCoin.sol";
import "../roles/DependsOnCurvePool.sol";
import "../roles/DependsOnFeeRecipient.sol";
import "../roles/RoleAware.sol";
import "../../interfaces/ICurvePool.sol";

/// This is a prototype of curve pool smart liquidity
/// As a prototype it isn't hardened against pool manipulations
/// hence only callable by whitelist
contract CurvePoolSL2 is
    DependsOnStableCoin,
    DependsOnCurvePool,
    DependsOnFeeRecipient,
    RoleAware
{
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    // amount of stablecoin deposited
    uint256 deposited;
    IERC20 counterparty = IERC20(0x5B5CFE992AdAC0C9D48E05854B2d91C73a003858);

    EnumerableSet.AddressSet internal whitelist;

    constructor(address _roles) RoleAware(_roles) {
        _rolesPlayed.push(MINTER_BURNER);
        Stablecoin(Roles(_roles).mainCharacters(STABLECOIN)).approve(
            Roles(_roles).mainCharacters(CURVE_POOL),
            type(uint256).max
        );
    }

    /// Main function of the contract
    /// checks ratio of reserves in pool and deposits or withdraws
    /// MONEY in order to restore balance
    function rebalance() external {
        require(isWhitelisted(msg.sender), "Not whitelisted");

        Stablecoin stable = stableCoin();
        address poolAddress = curvePool();
        ICurvePool pool = ICurvePool(poolAddress);

        uint256 stableBalance = stable.balanceOf(poolAddress);
        uint256 counterBalance = counterparty.balanceOf(poolAddress);

        uint256 lptSupply = IERC20(poolAddress).totalSupply();

        if (stableBalance * 100 > counterBalance * 101) {
            // we withdraw

            uint256 delta = stableBalance - counterBalance;
            uint256 lptBalance = IERC20(poolAddress).balanceOf(address(this));

            uint256 targetWithdrawAmount = min(
                lptBalance,
                (delta * lptSupply) / (stableBalance + counterBalance)
            );

            pool.remove_liquidity_one_coin(
                targetWithdrawAmount,
                0,
                (96 * targetWithdrawAmount * (stableBalance + counterBalance)) /
                    lptSupply /
                    100
            );
            uint256 actuallyWithdrawn = stable.balanceOf(address(this));

            if (deposited >= actuallyWithdrawn) {
                deposited -= actuallyWithdrawn;
            } else {
                stable.mint(feeRecipient(), actuallyWithdrawn - deposited);
                deposited = 0;
            }

            stable.burn(address(this), actuallyWithdrawn);
        } else if (counterBalance > stableBalance) {
            // we deposit

            uint256 delta = counterBalance - stableBalance;

            uint256 stableAvailable = stable.globalDebtCeiling() -
                stable.totalSupply();
            if (delta > stableAvailable) {
                delta = (2 * stableAvailable) / 3;
            }

            stable.mint(address(this), delta);

            uint256 minReturn = (96 * (delta * lptSupply)) /
                (stableBalance + counterBalance) /
                100;

            pool.add_liquidity([delta, 0], minReturn);

            deposited += delta;
        }

        stable.setMinBalance(poolAddress, (deposited * 2) / 3);
    }

    /// Checks whether an account is whitelisted
    function isWhitelisted(address caller) public returns (bool) {
        return
            caller == owner() ||
            caller == executor() ||
            caller == disabler() ||
            whitelist.contains(caller);
    }

    /// Add an account to the whitelist
    function add2Whitelist(address caller) external onlyOwnerExec {
        whitelist.add(caller);
    }

    /// Remove account from whitelist
    function removeFromWhitelist(address caller) external onlyOwnerExec {
        whitelist.remove(caller);
    }

    /// Send fees gained by protocol owned position to fee recipient
    function withdrawFees() external {
        require(isWhitelisted(msg.sender), "Not whitelisted");

        Stablecoin stable = stableCoin();
        address poolAddress = curvePool();
        ICurvePool pool = ICurvePool(poolAddress);

        uint256 lptBalance = IERC20(poolAddress).balanceOf(address(this));
        uint256 lptSupply = IERC20(poolAddress).totalSupply();

        uint256 stableBalance = stable.balanceOf(poolAddress);
        uint256 counterBalance = counterparty.balanceOf(poolAddress);

        uint256 gains = (lptBalance * (stableBalance + counterBalance)) /
            lptSupply -
            deposited;

        uint256 withdrawAmount = (gains * lptSupply) /
            (stableBalance + counterBalance);
        pool.remove_liquidity_one_coin(withdrawAmount, 0, (gains * 9) / 10);

        uint256 actualGains = stable.balanceOf(address(this));
        stable.mint(feeRecipient(), actualGains);
        stable.burn(address(this), actualGains);
    }

    /// Rescue stranded funds
    function rescueFunds(
        address token,
        address recipient,
        uint256 amount
    ) external onlyOwnerExec {
        IERC20(token).safeTransfer(recipient, amount);
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a >= b) {
            return b;
        } else {
            return a;
        }
    }
}
